	require(['querystring'], function(Querystring){
	    var obj = {
			foo : 1,
			bar : [2, 3]	
	    };
	    Querystring.stringify(obj); // foo=1&bar%5B%5D=2&bar%5B%5D=3 

	    var str = 'section=blog&tag=js&tag=doc';
	    Querystring.parse(str);  // {section: 'blog', tag: ['js', 'doc']}
	});

